// import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <div>
      	<h1>Verify phone</h1>
      	<form id="phone" action={sendSMS}>
			<label>enter phone number</label><br/>
      		<input name="phone"/><br/>
      		<button type="button">Send SMS</button>
      	</form>

		<form>
			<div className="form-group">
				<label htmlFor="phone">Phone</label>
				<input id="phone" name="phone" type="text" className="form-control" placeholder="Phone" onChange={changeHandler}/>
			</div>
			<span>&nbsp;&nbsp;&nbsp;</span>
			<button type="submit" className="btn btn-outline-success" onClick={sendSMS} disabled={loading}>Ð¡onfirm phone </button>
		</form>

      	<form id="verify">
      		<input name="twilio_code" />
      		<button>Verify</button>
      	</form>
      </div>
    </div>
  );
}

function sendSMS(){
	console.log('send sms');
}

export default App;
