import React, {useState} from 'react'
import {useHttp} from '../hooks/http.hook'

export const PhoneForm = () => {
	const {request} = useHttp()
	const [form, setForm] = useState({ phonenumber: '' })
	const changeHandler = event => { setForm({ ...form, [event.target.name]: event.target.value }) }

	const sendSMS = async () => {
		try {
			const data = await request('http://localhost:3000/phone', 'GET', {...form})
		} catch (e) {}
 	}

	return (
		<form>
			<div className="form-group">
				<label htmlFor="phonenumber">Phone</label>
				<input id="phonenumber" name="phonenumber" type="text" className="form-control" placeholder="Phone" onChange={changeHandler}/>
			</div>
			<span>&nbsp;&nbsp;&nbsp;</span>
			<button type="submit" className="btn btn-outline-success" onClick={sendSMS}>Ð¡onfirm phone </button>
		</form>
	)
}
