import React, {useState} from 'react'

export const PhoneForm = () => {
	const [form, setForm] = useState({ phonenumber: '' })
	const changeHandler = event => { setForm({ ...form, [event.target.name]: event.target.value }) }

	const sendSMS = async () => {
		try {
			const data = await fetch(`http://localhost:5000/phone?phonenumber=${form.phonenumber}`)
			console.log('data:', data);
		} catch (e) {}
 	}

	const sendCode = async () => {
		try {
			const data = await fetch(`http://localhost:5000/verify?code=${form.code}`)
			console.log('data:', data);
		} catch (e) {}
 	}

	return (
		<div>
			<form>
				<div className="form-group">
					<label htmlFor="phonenumber">Phone</label>
					<input id="phonenumber" name="phonenumber" type="text" className="form-control" placeholder="phone number" onChange={changeHandler}/>
				</div>
				<span>&nbsp;&nbsp;&nbsp;</span>
				<button type="submit" className="btn btn-outline-success" onClick={sendSMS}>Ð¡onfirm phone</button>

				<div className="form-group">
					<label htmlFor="code">Code</label>
					<input id="code" name="code" type="text" className="form-control" placeholder="code" onChange={changeHandler}/>
				</div>
				<span>&nbsp;&nbsp;&nbsp;</span>
				<button type="submit" className="btn btn-outline-success" onClick={sendCode}>Verify code</button>
			</form>
		</div>
	)
}
