import React, {useState} from 'react'

export const PhoneForm = () => {
	const [form, setForm] = useState({ phonenumber: '' })
	const changeHandler = event => { setForm({ ...form, [event.target.name]: event.target.value }) }

	const sendSMS = async () => {
		try {
			const data = await fetch(`http://localhost:5000/phone?phonenumber=${form.phonenumber}`)
			console.log('data:', data);
		} catch (e) {}
 	}

	return (
		<form>
			<div className="form-group">
				<label htmlFor="phonenumber">Phone</label>
				<input id="phonenumber" name="phonenumber" type="text" className="form-control" placeholder="{{form.phonenumber}}" onChange={changeHandler}/>
			</div>
			<span>&nbsp;&nbsp;&nbsp;</span>
			<button type="submit" className="btn btn-outline-success" onClick={sendSMS}>Ð¡onfirm phone</button>
		</form>
	)
}
