		<form>
			<div className="form-group">
				<label htmlFor="phonenumber">Phone</label>
				<input id="phonenumber" name="phonenumber" type="text" className="form-control" placeholder="Phone" />
			</div>
			<span>&nbsp;&nbsp;&nbsp;</span>
			<button type="submit" className="btn btn-outline-success" onClick={sendSMS}>Ð¡onfirm phone </button>
		</form>

      	<form id="verify">
      		<input name="twilio_code" />
      		<button>Verify</button>
      	</form>
      </div>
    </div>
  );
}

async function sendSMS(){
	console.log('send sms')

    try {
      const data = await request('http://localhost:3000/send', 'GET', {...form})
		console.log('data:', data)
    } catch (e) {}

}

export default App;
